import javax.swing.JOptionPane;

public class OrdenaFrase {
	
	/**
	 * @param args
	 */
	
	public static void main(String[] args) {
		
		int continuar = 0;
		
		
		do {
						
			String frase = JOptionPane.showInputDialog("Digite a frase: ");
			String fraseOrdenada = contarPalavras(frase);
			
			JOptionPane.showMessageDialog(null, " Resultado: " +fraseOrdenada, "resultado", JOptionPane.INFORMATION_MESSAGE);

			continuar = JOptionPane.showConfirmDialog(null, "Deseja continuar?");
			
		} while(continuar == JOptionPane.YES_OPTION);		
		
	}
	
	/**
	 * Conta as colunas e ordena em forma crescente.
	 * 
	 * @param frase   frase atual a ser ordenada.
	 * @return		  frase ordenada.
	 * @autor         João Pedro,Nathan.
	 */

	public static String contarPalavras(String frase) {
		
		String contador[] = frase.split(" ");
		String fraseOrdenada = "";
		
		
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     		
	for (int i = 0; i < contador.length; i++) {
				
		for (int j = 0; j < contador.length-1; j++){		
			 if(contador[j].length() > contador[j+1].length()){
						
						String cont = contador[j+1];
						contador[j+1]= contador[j];
						contador[j]= cont;	
			 }
		}			
	 
	}	
	
	for (int i = 0; i < contador.length; i++) {
		
		fraseOrdenada +=" "+contador[i];
		
	}
	

	return fraseOrdenada.trim();

	}

}